def determine_lever_balance(s: str) -> str:
    suc = 1
    kde = 0
    for i in range(len(s)):
        if s[i] == '^':
            kde = i
            break
    for i in range(len(s)):
        c = s[i]
        if '1' <= c <= '9':
            suc += (kde - i) * int(c)
    if suc < 0:
        return 'right'
    elif suc > 0:
        return 'left'
    else:
        return 'balance'

