    graph = [[] for _ in range(n + 1)]
    for (u, v) in edges:
        graph[u].append(v)
        graph[v].append(u)
    link = [[-1 for _ in range(n + 2)] for _ in range(n + 1)]
    for i in range(1, n + 1):
        link[i][i] = 0
        q = deque()
        q.append(i)
        while q:
            u = q.popleft()
            for v in graph[u]:
                if link[i][v] >= 0:
                    continue
                link[i][v] = link[i][u] + 1
                q.append(v)
    ans = 0
    for root in range(1, n + 1):
        ans += calc(n, link, start=root)
        ans %= M
    ans *= inv(n)
    ans %= M
    return ans

