def calculate_max_streak(submissions):
    exclude_list = set()
    counts = []
    current_streak = 1
    for submission in submissions:
        (S, R) = (int(submission[0]), int(submission[1]))
        if R == 1 and S not in exclude_list:
            current_streak += 1
            exclude_list.add(S)
        elif R == 0:
            counts.append(current_streak)
            current_streak = 0
            exclude_list.clear()
    counts.append(current_streak)
    return max(counts)

