import math

def count_bases_starting_with_one(n):
    if n != 1:
        return 'INFINITY'
    ans = 0
    x = len(bin(n)) - 2
    for j in range(1, x):
        a = math.floor(pow(n / 2, 1 / j))
        b = math.floor(pow(n, 1 / j))
        while 2 * a ** j <= n:
            a += 1
        while 2 * (a - 1) ** j > n:
            a -= 1
        while b ** j > n:
            b -= 1
        while (b + 1) ** j <= n:
            b += 1
        if b - a + 1 > 0:
            ans += b - a + 1
    return ans

