def is_convex_quadrangle(points):

    def gai(a, b, c, d):
        return a * d - b * c
    ((x1, y1), (x2, y2), (x3, y3), (xp, yp)) = points

    def check_convexity(p1, p2, p3, p4):
        return gai(p1[0] - p2[0], p1[1] - p2[1], p1[0] - p4[0], p1[1] - p4[1]) < 0 and gai(p2[0] - p3[0], p2[1] - p3[1], p2[0] - p4[0], p2[1] - p4[1]) < 0 and (gai(p3[0] - p1[0], p3[1] - p1[1], p3[0] - p4[0], p3[1] - p4[1]) < 0) or (gai(p1[0] - p2[0], p1[1] - p2[1], p1[0] - p4[0], p1[1] - p4[1]) > 0 and gai(p2[0] - p3[0], p2[1] - p3[1], p2[0] - p4[0], p2[1] - p4[1]) > 0 and (gai(p3[0] - p1[0], p3[1] - p1[1], p3[0] - p4[0], p3[1] - p4[1]) > 0))
    permutations = [(points[0], points[1], points[2], points[3]), (points[0], points[1], points[3], points[2]), (points[0], points[3], points[2], points[1]), (points[3], points[1], points[2], points[0])]
    for perm in permutations:
        if check_convexity(*perm):
            return 'NO'
    return ''

