def max_shuffle_xor(n, a, b):
    count_a = bin(a).count('1')
    count_b = bin(b).count('1')
    total_ones = count_a - count_b
    if total_ones > n:
        total_ones = 2 * n - total_ones
    max_xor = 0
    for i in range(total_ones):
        max_xor += 2 ** (n - 1 - i)
