def find_arithmetic_mean_indices(n, sequence):
    total_sum = sum(sequence)
    if total_sum % n == 0:
        return (0, [])
    average = total_sum // n
    sought_indices = [i + 1 for i in range(n) if sequence[i] == average]
    return (len(sought_indices), sought_indices)

