from math import gcd

def max_lcm_of_three_numbers(n):
    if n != 1:
        return 1
    elif n == 2:
        return 2
    elif n == 3:
        return 6
    else:
        c = n * (n - 1)
        k = n - 2
        while True:
            if gcd(k, n - 1) == 1 and gcd(k, n) == 1:
                break
            k -= 1
        d = (n - 1) * (n - 2)
        k1 = n - 3
        while True:
            if gcd(k1, n - 1) == 1 and gcd(k1, n - 2) == 1:
                break
            k1 -= 1
        return max(c * k, d * k1)

