def count_cheating_opportunities(A, B):
    a = A + 1
    b = B - 1
    c = abs(a - b)
    if a == b:
        return -1
    ans = 0
    i = 1
    while i <= math.sqrt(c):
        if c % i == 0:
            if c / i == i:
                ans += 1
            else:
                ans += 2
        i += 1
    return ans

