def calculate_illumination_length(W, H, map_layout):
    dx = [[1, 1, 1, 0, -1, 0], [0, 1, 0, -1, -1, -1]]
    dy = [-1, 0, 1, 1, 0, -1]

    def dfs(x, y):
        if map_layout[y][x] != 0:
            return
        map_layout[y][x] = 2
        for (xx, yy) in zip(dx[y % 2], dy):
            (tx, ty) = (x + xx, y + yy)
            if 0 <= tx < W and 0 <= ty < H:
                dfs(tx, ty)
    for x in range(W):
        dfs(x, 0)
        dfs(x, H - 1)
    for y in range(H):
        dfs(0, y)
        dfs(W - 1, y)
    n = 0
    for (x, y) in product(range(W), range(H)):
        if map_layout[y][x] != 1:
            continue
        for (xx, yy) in zip(dx[y % 2], dy):
            (tx, ty) = (x + xx, y + yy)
            if 0 <= tx < W and 0 <= ty < H:
                if map_layout[ty][tx] == 2:
                    n += -1
            else:
                n += 1
    return n
