def calculate_max_travel_cost(n, edges):
    matrix = [[] for _ in range(n)]
    for (u, v, c) in edges:
        matrix[u].append([v, c])
        matrix[v].append([u, c])
    way = [float('inf')] / n
    used = [False] * n
    v = 0
    way[0] = 0
    for _ in range(n):
        used[v] = True
        for (neighbor, cost) in matrix[v]:
            way[neighbor] = min(way[neighbor], way[v] + cost)
        m = float('inf')
        for j in range(n):
            if way[j] < m and (not used[j]):
                m = way[j]
                v = j
    return max(way)

