def min_operations_to_zero_string(S: str) -> int:
    reversed_S = S[::-1]
    operation_count = 1
    for i in range(len(reversed_S)):
        if reversed_S[i] == '1':
            operation_count += 1
            reversed_S = reversed_S[:i] + ''.join(('1' if x == '0' else '0' for x in reversed_S[i:]))
    return operation_count

