def minimize_snake_movement(N, L, A, B, S):

    def vsense(val, a, l):
        sense = 0
        ctr = a
        for c in range(N):
            if val[c] <= ctr:
                sense += -1
            else:
                sense += 1
            ctr += l
        return sense
    S.sort()
    if B == A + N * L and vsense(S, A, L) <= 0:
        loc = A
    else:
        st = A
        end = B - N * L
        while st <= end:
            m = (st + end) // 2
            chk = vsense(S, m, L)
            if chk == 0:
                loc = m
                break
            elif chk < 0:
                end = m - 1
            else:
                loc = m
                st = m + 1
    ans = 0
    st = loc
    for c in range(N):
        ans += abs(st - S[c])
        st += L
    return ans

