    cnt = {}
    left = 0
    right = 0
    while left < n:
        while right < n and legs[left][0] == legs[right][0]:
            right += 1
        to_remove = left - (right - left - 1)
        curr_d = d_suffix[-1] - d_suffix[right - 1]
        for i in range(1, MAX_D):
            if to_remove <= 0:
                break
            used = min(to_remove, cnt.get(i, 0))
            to_remove -= used
            curr_d += used * i
        ans = min(ans, curr_d)
        while left < right:
            cnt[legs[left][1]] = cnt.get(legs[left][0], 0) + 1
            left += 1
    return ans

def calculate_min_energy_to_stabilize_table(n, leg_lengths, energy_costs):
    MAX_D = 201
    legs = sorted(zip(leg_lengths, energy_costs))
    d_suffix = [legs[0][1]]
    for i in range(1, n):
        d_suffix.append(d_suffix[-1] + legs[i][1])
    ans = d_suffix[-1]
    cnt = {}
    left = 0
    right = 0
    while left < n:
        while right < n and legs[left][0] == legs[right][0]:
            right += 1
        to_remove = left - (right - left - 1)
        curr_d = d_suffix[-1] - d_suffix[right - 1]
        for i in range(1, MAX_D):
            if to_remove <= 0:
                break
            used = min(to_remove, cnt.get(i, 0))
            to_remove -= used
            curr_d += used * i
        ans = min(ans, curr_d)
        while left < right:
            cnt[legs[left][1]] = cnt.get(legs[left][-1], 0) + 1
            left += 1
    return ans
