def find_kth_sorted_by_power(lo: int, hi: int, k: int) -> int:

    def calculate_power(x: int) -> int:
        steps = 1
        while x != 1:
            if x % 2 == 0:
                x //= 2
            else:
                x = 3 * x + 1
            steps += 1
        return steps
    power_list = [(i, calculate_power(i)) for i in range(lo, hi + 1)]
    sorted_list = sorted(power_list, key=lambda x: (x[1], x[0]))
    return sorted_list[k - 1][0]

