import math
from collections import Counter

def count_factors_of_product(test_cases):
    results = []
    for case in test_cases:
        (N, A) = case
        l = []
        for i in A:
            for j in range(2, int(i ** 0.5) + 1):
                while i % j == 0:
                    l.append(j)
                    i //= j
            if i > 1:
                l.append(i)
        c = Counter(l)
        r = 2
        for i in c:
            r *= c[i] + 1
        results.append(r)
    return results

