    if k - 1 <= n - k:
        for _ in range(k - 1):
            actions.append('LEFT')
        for i in range(n - 1):
            actions.append(f'PRINT {slogan[i]}')
            actions.append('RIGHT')
        actions.append(f'PRINT {slogan[-1]}')
    else:
        for _ in range(n - k):
            actions.append('RIGHT')
        for i in range(n - 1):
            actions.append(f'PRINT {slogan[-(i + 1)]}')
            actions.append('LEFT')
        actions.append(f'PRINT {slogan[1]}')
    return actions

