def count_ways_to_cook(N, K, MOD=1000000008):
    Kmod = K % MOD
    K1mod = (K - 1) % MOD
    K2mod = (K - 2) % MOD
    if K == 1:
        return 0
    elif K == 2:
        return 2
    else:
        inner = (Kmod * K1mod * K1mod % MOD + Kmod * K1mod * K2mod * K2mod % MOD) % MOD
        if N == 1:
            return K * (K - 1) % MOD
        elif N == 2:
            return inner
        else:
            return inner * pow(K ** 2 - 3 * K + 3, N - 2, MOD) % MOD

