def find_polynomial_modulo(p: int, k: int):
    if k <= 2:
        return -1
    coefficients = []
    while p:
        coefficients.append((p % k + k) % k)
        p = (p % k - p) // k
    if not coefficients:
        return -1
    return (len(coefficients), coefficients)

