def count_spiral_turns(x: int, y: int) -> int:
    if x == 0 or y == 0:
        return 0
    current_x = 0
    current_y = 0
    step = 1
    direction = 0
    turns = 0
    while current_x != x or current_y != y:
        if direction == 0:
            for _ in range(step):
                current_x += 1
                if current_x == x and current_y == y:
                    return turns
            direction = 1
            turns += 1
        elif direction == 1:
            for _ in range(step):
                current_y += 1
                if current_x == x and current_y == y:
                    return turns
            direction = 2
            step += 1
            turns += 1
        elif direction == 2:
            for _ in range(step):
                current_x -= 1
                if current_x == x and current_y == y:
                    return turns
            direction = 3
            turns += 1
        else:
            for _ in range(step):
                current_y -= 1
                if current_x == x and current_y == y:
                    return turns
            direction = 0
            step += 1
            turns += 1
    return turns

