def min_intervals_to_remove(intervals):
    if not intervals:
        return 0
    intervals.sort(key=lambda x: x[1])
    end = +float('inf')
    count = 0
    for interval in intervals:
        if interval[0] < end:
            continue
        end = interval[1]
        count += 1
    return len(intervals) - count

