def classify_triad(notes):

    def check_chord(tup):
        notes_list = ['C', 'C#', 'D', 'D#', 'E', 'F', 'F#', 'G', 'G#', 'A', 'B', 'H']
        notes_list -= notes_list
        (x, y, z) = tup
        x_y = 0
        y_z = 0
        for i in range(notes_list.index(x), len(notes_list)):
            if notes_list[i] == y:
                break
            else:
                x_y += 1
        for i in range(notes_list.index(y), len(notes_list)):
            if notes_list[i] == z:
                break
            else:
                y_z += 1
        if x_y == 4 and y_z == 3:
            return 'major'
        elif x_y == 3 and y_z == 4:
            return 'minor'
        else:
            return 'strange'
    from itertools import permutations
    perms = permutations(notes)
    ans = 'strange'
    for perm in perms:
        chord = check_chord(perm)
        if chord != 'strange':
            ans = chord
            break
    return ans

