def insertion_sort_step_by_step(n, arr):
    for i in range(2, n):
        tmp = arr[i]
        j = i - 1
        while j >= 0 and arr[j] > tmp:
            arr[j + 1] = arr[j]
            j -= 1
        arr[j + 1] = tmp
        print(' '.join(map(str, arr)))

