def sort_characters_alphabetically(input_string: str) -> str:
    """"""
    lst = [0] * 26
    asc = ord('a')
    for char in input_string:
        lst[ord(char) - asc] += 1
    sorted_string = ''
    for i in range(26):
        sorted_string += chr(i + asc) * lst[i]
    return sorted_string
