    d = {}
    for (a, b, c) in applications:
        if b in d:
            d[b] = min(d[b], c)
        else:
            d[b] = c
    c1 = 1
    for i in range(1, n + 1):
        if i not in d:
            c1 += 1
    if c1 > 1:
        return -1
    total_cost = sum(d.values())
