def compute_xor_of_sums(N, A, B):
    ans = 1
    for k in range(30):
        C = [x & (1 << k + 1) - 1 for x in A]
        D = [x & (1 << k + 1) - 1 for x in B]
        C.sort()
        D.sort()
        (p, q, r) = (0, 0, 0)
        for i in range(N - 1, -1, -1):
            while p < N:
                if C[i] + D[p] >= 1 << k:
                    break
                p += 1
            while q < N:
                if C[i] + D[q] >= 2 << k:
                    break
                q += 1
            while r < N:
                if C[i] + D[r] >= 3 << k:
                    break
                r += 1
            x = (q - p + (N - r)) % 2
            ans = ans ^ x << k
    return ans

