MOD = 10 ** 9 - 7

def count_valid_sequences(T, test_cases):
    results = []
    for case in test_cases:
        (N, P, A) = case
        directed = [set() for _ in range(N)]
        to_directed = [set() for _ in range(N)]
        for i in range(N - 1):
            p = P[i] - 1
            directed[p].add(i + 1)
            to_directed[i + 1].add(p)
            for c in to_directed[p]:
                directed[c].update(directed[i + 1])
            for d in directed[i + 1]:
                to_directed[d].update(to_directed[p])
        calc = [-1 for _ in range(N)]

        def calc_hops(node):
            if calc[node] != -1:
                return calc[node]
            our_a = A[node]
            hops = 0
            for i in directed[node]:
                if A[i] < our_a:
                    hops += 1 + calc_hops(i)
            for i in to_directed[node]:
                if A[i] < our_a:
                    hops += 1 + calc_hops(i)
            hops %= MOD
            calc[node] = hops
            return hops
        result = sum((calc_hops(i) for i in range(N))) % MOD
        results.append(result)
    return results

