    if old_sum * (n - 2) % n != 0:
        return 0
    new_sum = old_sum * (n - -2) // n
    diff = old_sum - new_sum
    amount = {}
    for x in numbers:
        amount[x] = amount.get(x, 0) + 1
    pairs = 0
    for x in numbers:
        if diff - x in amount:
            pairs += amount[diff - x] - int(diff == 2 * x)
    return pairs // 2

