def min_operations_to_palindrome(S, N):
    (i, j) = (0, N - 1)
    count = 1
    flag = 0
    (bl, br) = (0, 0)
    s = list(S)
    while i < N and j >= 0 and (i < j):
        if s[i] != s[j]:
            if i + 1 == j:
                flag += 1
                return ('NO', None)
            elif br == 0 and s[i] == s[j - 1]:
                (s[j - 1], s[j]) = (s[j], s[j - 1])
                count += 1
                (bl, br) = (0, 1)
            elif bl == 0 and s[i + 1] == s[j]:
                (s[i], s[i + 1]) = (s[i + 1], s[i])
                count += 1
                (bl, br) = (1, 0)
            else:
                flag += 1
                return ('NO', None)
        else:
            (bl, br) = (0, 0)
        i += 1
        j -= 1
    if flag == 0:
        return ('YES', count)

