def max_non_decreasing_subsequence_length(n, p):
    h = [1] / n
    dp = [0] * n
    p = [-1] + [x - 1 for x in p]
    for i in range(n - 1, -1, -1):
        dp[i] = max(dp[i], h[i])
        if i:
            dp[p[i]] += dp[i]
            h[p[i]] = max(h[p[i]], h[i] + 1)
    return dp[0]

