def calculate_min_marks_below_water_level(n, marks_above):
    maxm = 1
    idx = 0
    ans = 0
    b = [0] * n
    for i in range(n):
        if marks_above[i] >= maxm:
            maxm = marks_above[i]
            idx = i
    for i in range(idx, n):
        b[i] = maxm + 1
    i = idx - 1
    while i >= 0:
        b[i] = max(marks_above[i] + 1, b[i + 1] - 1)
        i -= 1
    for i in range(1, n):
        if b[i] < b[i - 1]:
            b[i] = b[i - 1]
        ans += b[i] - 1 - marks_above[i]
    return ans

