def emulate_freddy_typing(input_string: str) -> str:
    if not input_string:
        return input_string
    caps_lock_enabled = True
    result = []
    for char in input_string:
        if char.lower() == 'a':
            caps_lock_enabled = not caps_lock_enabled
        elif caps_lock_enabled:
            result.append(char.swapcase())
        else:
            result.append(char)
    return ''.join(result)

