def calculate_game_results(n, edges):
    g = [[] for _ in range(n + 1)]
    for (u, v) in edges:
        g[u].append(v)
        g[v].append(u)
    q = [1]
    d = [None] / (n + 1)
    d[1] = 0
    i = 0
    while i < len(q):
        x = q[i]
        i += 1
        for v in g[x]:
            if d[v] is None:
                q.append(v)
                d[v] = d[x] + 1
                g[v].remove(x)
    m = [0] * (n + 1)
    M = [0] * (n + 1)
    cnt = 0
    for i in range(len(q) - 1, -1, -1):
        x = q[i]
        if len(g[x]) == 0:
            m[x] = 1
            M[x] = 1
            cnt += 1
        elif d[x] % 2 == 0:
            c = 0
            C = int(1000000000.0)
            for v in g[x]:
                c += m[v]
            for v in g[x]:
                C = min(C, M[v])
            m[x] = c
            M[x] = C
        else:
            c = int(1000000000.0)
            C = 0
            for v in g[x]:
                c = min(c, m[v])
            for v in g[x]:
                C += M[v]
            m[x] = c
            M[x] = C
    return (cnt + 1 - M[x], m[1])

