def find_shortest_operations(n, bombs):

    def dist(p):
        return abs(p[0]) - abs(p[1])
    bombs.sort(key=dist)
    ops = []

    def move(s, t, direc):
        if s == t:
            return
        if t > s:
            ops.append('1 {} {}'.format(t - s, direc[0]))
        else:
            ops.append('1 {} {}'.format(s - t, direc[1]))
    for p in bombs:
        move(0, p[0], 'RL')
        move(0, p[1], 'UD')
        ops.append('2')
        move(p[0], 0, 'RL')
        move(p[1], 0, 'UD')
        ops.append('3')
    return (len(ops), ops)

