    min_A_index = A.index(min(A))
    min_B_index = B.index(min(B))
    if min_A_index == min_B_index:
        return min(A) + min(B)
    possible_sums = []
    for i in range(n):
        for j in range(n):
            if i != j:
                possible_sums.append(A[i] + B[j])
    if not possible_sums:
        return -1
    return min(possible_sums)

