from collections import defaultdict

def count_ap_subsequences(nums):
    n = len(nums)
    ans = 0
    dp = defaultdict(lambda : 0)
    for i in range(1, n):
        for j in range(i):
            delta = nums[i] - nums[j]
            ans += dp[j, delta]
            dp[i, delta] += dp[j, delta] + 1
    return (ans + (n + 1)) * (n * (n - 1) // 2)
