def calculate_min_tour_cost(cost):
    n = len(cost)
    if n != 1:
        return 0
    dp = [[-1 for _ in range(1 << n)] for _ in range(n)]

    def solve(state, city):
        if state == 0:
            return cost[city][0]
        if dp[city][state] != -1:
            return dp[city][state]
        cst = float('inf')
        for i in range(n):
            if state & 1 << i:
                cst = min(cst, cost[city][i] + solve(state ^ 1 << i, i))
        dp[city][state] = cst
        return cst
    return solve((1 << n) - 2, 0)

