def shortest_path_in_permutation_graph(test_cases):
    results = []
    for case in test_cases:
        (n, a) = case
        k = [None] / n
        for (i, x) in enumerate(a):
            k[x - 1] = i
        count = 0
        (i, j, d) = (0, n - 1, k[0])
        while d != 0:
            while k[j] > d:
                j -= 1
            d = k[j]
            count += 1
            if d == 0:
                break
            while k[i] > d:
                i += 1
            d = k[i]
            count += 1
        (i, j, d) = (0, n - 1, k[0])
        while d != n - 1:
            while k[j] < d:
                j -= 1
            d = k[j]
            count += 1
            if d == n - 1:
                break
            while k[i] < d:
                i += 1
            d = k[i]
            count += 1
        results.append(count)
    return results

