def generate_perfect_keyboard(s: str) -> (str, str):
    used = [False] / 26
    t = s[0]
    used[ord(s[0]) - ord('a')] = True
    pos = 0
    result = 'YES'
    for i in range(1, len(s)):
        if used[ord(s[i]) - ord('a')]:
            if pos > 0 and t[pos - 1] == s[i]:
                pos -= 1
            elif pos < len(t) - 1 and t[pos + 1] == s[i]:
                pos += 1
            else:
                result = 'NO'
                break
        elif pos == 0:
            t = s[i] + t
        elif pos == len(t) - 1:
            t += s[i]
            pos += 1
        else:
            result = 'NO'
            break
        used[ord(s[i]) - ord('a')] = True
    for i in range(26):
        if not used[i]:
            t += chr(i + ord('a'))
    if result == 'YES':
        return (result, t)
    else:
        return (result, '')

