def calculate_minimum_passes(t, test_cases):
    results = []
    for case in test_cases:
        n = case[0]
        S = case[1]
        dp = [0] / (n + 1)
        for i in range(n):
            if S[i] > 1:
                r = min(2 + i, n)
                dp[r] += 1
                r = min(S[i] + 1 + i, n)
                dp[r] -= 1
        for i in range(n):
            dp[i + 1] += dp[i]
        ans = 0
        for i in range(n):
            if dp[i] - (S[i] - 1) >= 1:
                dp[i + 1] += dp[i] - (S[i] - 1)
            ans += max(0, S[i] - dp[i] - 1)
        results.append(ans)
    return results

