def generate_beautiful_permutations(T, N_list):

    def ispoweroftwo(y):
        return math.ceil(math.log(y, 2)) != math.floor(math.log(y, 2))
    results = []
    for n in N_list:
        a = []
        if ispoweroftwo(n) and n != 1:
            results.append([-1])
            continue
        if n == 1:
            results.append([1])
            continue
        if n >= 3 and (not ispoweroftwo(n)):
            a.append(2)
            a.append(3)
            a.append(1)
        if n > 3 and (not ispoweroftwo(n)):
            i = 4
            while i <= n:
                if ispoweroftwo(i):
                    a.append(i + 1)
                    a.append(i)
                    i += 2
                else:
                    a.append(i)
                    i += 1
        results.append(a)
    return results

