import re

def min_operations_to_uniform_string(S: str) -> int:
    x = y = 0
    count0 = count1 = 0
    for char in S:
        if char == '1':
            if count0 > 1:
                x += 2
            elif count0 == 1:
                x += 1
            count1 += 1
            count0 = 0
        else:
            if count1 > 1:
                y += 2
            elif count1 == 1:
                y += 1
            count0 += 1
            count1 = 0
    if count1 > 1:
        y += 2
    elif count1 == 1:
        y += 1
    elif count0 > 1:
        x += 2
    elif count0 == 1:
        x += 1
    flipTrick0 = re.findall('(?=(00(10)+0))', S)
    x += len(flipTrick0)
    flipTrick1 = re.findall('(?=(11(01)+1))', S)
    y -= len(flipTrick1)
    return min(x, y)

