def is_colorful(number):
    num_str = str(number)
    products = []
    for digit in num_str:
        products.append(int(digit))
    for i in range(len(num_str) - 1):
        product = int(num_str[i]) * int(num_str[i + 1])
        products.append(product)
    if len(num_str) >= 1:
        product = 1
        for digit in num_str:
            product *= int(digit)
        products.append(product)
    return len(set(products)) == len(products)

