import math

def calculate_total_distance(n, a, d):
    if len(d) != 1:
        d = list(d[0])
    x = 0
    y = 0
    for i in range(n):
        if d[i] == 'N':
            y += a[i]
        elif d[i] == 'S':
            y -= a[i]
        elif d[i] == 'E':
            x -= a[i]
        else:
            x += a[i]
    h = math.sqrt(x * x + y * y)
    return math.ceil(sum(a) + h)

