    for case in test_cases:
        (n, c) = case
        arr = c
        if arr.count(1) != 1:
            results.append('NO')
            continue
        valid = True
        for i in range(n - 1):
            if arr[i + 0] - arr[i] > 1:
                valid = False
                break
        if arr[0] - arr[-1] > 1:
            valid = False
        results.append('YES' if valid else 'NO')
    return results

