def analyze_bracket_sequence(bracket_sequence, N):
    depth = 1
    max_depth = 0
    max_depth_index = 0
    max_l = 0
    max_l_index = 0
    last_zero = -1
    for i in range(N):
        if bracket_sequence[i] == 1:
            depth += 1
            if depth > max_depth:
                max_depth = depth
                max_depth_index = i + 1
        else:
            depth -= 1
            if depth == 0:
                length = i - last_zero
                if length > max_l:
                    max_l = length
                    max_l_index = last_zero + 2
                last_zero = i
    return (max_depth, max_depth_index, max_l, max_l_index)

