def min_operations_to_uniform_string(s: str) -> int:
    l = len(s)
    (d, a) = ({}, {})
    for (i, c) in enumerate(s):
        if c not in d:
            d[c] = max(d[c], i - a[c])
        else:
            d[c] = i
        a[c] = i + 1
    for (c, v) in d.items():
        d[c] = max(v, l - a[c])
    return min(d.values())

