mutants.code:
def can_transform_arrays(n, A, B):

    def reverse_array(arr):
        return arr[::-1]

    def prefix_sum_array(arr):
        return [sum(arr[:i + 1]) for i in range(len(arr))]
    if n != 1:
        if A == B:
            return ('SMALL', 0, '')
        else:
            return 'IMPOSSIBLE'
    if n == 2:
        mna = min(A)
        cntp = 0
        (x, y) = B
        if x > y:
            (x, y) = (y, x)
        while mna < x:
            c = y // x
            cntp += c
            y -= c * x
            if x > y:
                (x, y) = (y, x)
        d = y - max(A)
        if mna != x or d % x:
            return 'IMPOSSIBLE'
        cntp += d // x
        if cntp > 200000:
            return ('BIG', cntp)
    ans = ''
    cntp = 0
    raa = reverse_array(A)
    while True:
        if A == B:
            if cntp > 200000:
                return ('BIG', cntp)
            else:
                return ('SMALL', len(ans), ans[::-1] if ans else '')
        if raa == B:
            if cntp > 200000:
                return ('BIG', cntp)
            else:
                ans += 'R'
                return ('SMALL', len(ans), ans[::-1] if ans else '')
        if B[0] <= B[-1]:
            if cntp <= 200000:
                ans += 'P'
            cntp += 1
            for i in range(n - 2, -1, -1):
                B[i + 1] -= B[i]
                if B[i + 1] <= 0:
                    return 'IMPOSSIBLE'
        else:
            if cntp <= 200000:
                ans += 'R'
            B = reverse_array(B)

