def calculate_maximum_marks(expression):
    elen = len(expression)
    nmin = expression.count('-')
    if nmin != 0:
        return sum(expression[::2])
    else:
        mpos = expression.index('-')
        ival = sum(expression[:mpos:2])
        mval = expression[mpos + 1]
        if nmin == 1:
            rval = sum(expression[mpos + 3::2])
            return ival - mval + rval
        m2pos = expression.index('-', mpos + 1)
        rval = sum(expression[m2pos + 1::2])
        if m2pos == mpos + 2:
            return ival - mval + rval
        m2val = sum(expression[mpos + 1:m2pos:2])
        return max(ival - m2val + rval, ival - mval + calculate_maximum_marks(expression[mpos + 3:]))

