def arrange_numbers_on_circle(n):
    x = 2 * n
    ans = [0] * x
    num = 1
    bum = x
    for i in range(n):
        if i % 2 == 0:
            ans[i] = num
            ans[(i + n) % x] = num + 1
            num += 2
        else:
            ans[i] = bum
            ans[(i + n) % x] = bum - 1
            bum -= 2
    this = ans.copy()
    this -= this
    for i in range(1, 2 * x):
        this[i] += this[i - 1]
    these = set()
    for i in range(n - 1, 2 * x):
        if i == n - 1:
            these.add(this[i])
        else:
            these.add(this[i] - this[i - n])
    if len(these) == 1:
        return ('YES', ans)
    elif len(these) == 2:
        xx = list(these)
        xxx = abs(xx[0] - xx[1])
        if xxx <= 1:
            return ('YES', ans)
    return ('NO', [])

